	If ('((..StorageURL'="") ! (..CDNMURL'="")) ! '(..AuthToken'="")) {
		Set ret=..#FALSE
    }
    Quit ret

UDLText.Content:
/**
     * Toggle debugging - set cURL verbose flag
     */

/**
