		 Set path=path_$LB($$$URLENCODE(containerName))
	 }

	 if ($GET(objectName,"") '= "") {
		 // TODO: do we need to replace %2F with "/" ? (see below)
		 Set path=path_$LB($$$URLENCODE(objectName))
	 }

	 /* if ($o) {
            # mimic Python''s urllib.quote() feature of a "safe" '/' character
