Set urlPath = ..makePath(..#CCFPathTypeStorage, containerName)

Set limit=(+limit)

Set params = $LB(..#CCFHTTPReqStringFormat_..#CCFHTTPReqStringAssign_..#CCFHTTPReqStringFormatXML)

