	Set params=params_$LB(..#CCFHTTPReqStringLimit_..#CCFHTTPReqStringAssign_limit)
}

If (marker '= "") {
	Set params=params_$LB(..#CCFHTTPReqStringMarker_..#CCFHTTPReqStringAssign_$$$URLENCODE(marker))
