			If ('(cspAppName?1.(1A,1N,1"-"))) { //Alphanumeric and dash (-) are allowed.
				Write !,"Invalid characters in CSP App Name. Must be alphanumeric. Please retry."
				Set gotCSPAppName=0
			} else {
				Write !,"CSP App Name = "_cspAppName
