	if simulate {
		w !,"simulation:"_newNameSpace_" DB would be created."
	} else {
		do ..create(newNameSpace,cspAppName,0,"",addToMirror)
	}
	set atLeastOneCreated=1

	if removeZCSPAPPS kill ^%ZCSPAPPS(newNameSpace)
}
if (atLeastOneCreated) {
	do ..activateCPF()
}
