Query.SqlQuery:
SELECT count(ID) AS xCount,{fn CONVERT(xCRstamp,SQL_DATE)} as xDate
FROM abstract.viewSubmittedAbstracts WHERE (objTopic->objEvent = :eventID) 
GROUP BY {fn CONVERT(xCRstamp,SQL_DATE)}

Method.Implementation:
set eventID=..objEvent.%Id() set wizardID=##class(eventWizard.objWizard).getWizardForEvent(eventID) if 'wizardID quit set actionTotal=0 &SQL(SELECT COUNT(ID) INTO :tmp FROM eventWizard.lnkWizardAction WHERE objWizard = :wizardID ) if +SQLCODE=0 { set actionTotal=tmp }
set comments="",description="None" &SQL(SELECT comments,description INTO :tmp,:tmp2 FROM eventWizard.objWizard WHERE ID = :wizardID ) if +SQLCODE=0 { set comments=tmp,description=tmp2 } set showAll=%request.Get("actionListStatusDisp")="all"
set actionCount=0 set hasMandatory=0 do ..addSection("")
set rsWizActions=##class(%ResultSet).%New("eventWizard.objWizard:qAllWizardActions") do rsWizActions.Execute(wizardID) while rsWizActions.Next() { set actionCount=actionCount+1 set actionID=rsWizActions.Get("objAction") set isMandatory=rsWizActions.Get("isMandatory") if isMandatory set hasMandatory=1 set comments=rsWizActions.Get("comments")
set notes="" set status="not started" &SQL(SELECT status,notes INTO :tmp,:tmp2 FROM eventWizard.lnkActionEvent WHERE objEvent = :eventID AND objAction = :actionID ) if +SQLCODE=0 { set status=$ZCVT(tmp,"L"),notes=tmp2 } set description=rsWizActions.Get("description") set summaryItem=..addRow(description,status,"action") set summaryItem.isMandatory = isMandatory set summaryItem.accessID = rsWizActions.Get("accessID") }


set rsCats=##class(%ResultSet).%New("links.lnkPersonPurchase:qCountPerAttendeeCategory")
do rsCats.Execute(..eventID)
while rsCats.Next() {
