	Set integClass=$LISTGET(integClasses,idx)
	Try {
		Set integ=$CLASSMETHOD(integClass,"createWithDefaultConnection")
		If ($IsObject(integ)) {
			Do ret.Insert(integ)
		}
	} catch ex {
		// ignore
	}
}

Quit ret

