if (##class(EF.config.server).isThisLive())!(useLiveEmailAddress) {
	do mailMessage.To.Insert("saleslicense@eventsforce.com")
} else {
	set toAddress=""
	for {
		set toAddress=$o(^%xSendWarningEmailsTest(toAddress))
		if toAddress="" quit
		do mailMessage.To.Insert(toAddress)
	}
}

