&SQL(SELECT ID
	FROM EF_licensing.objLicense
	WHERE (
		(startTimeStampUTC<=:startTimeStampUTC)
		AND (endTimeStampUTC>=:startTimeStampUTC)
	) OR (
		(startTimeStampUTC<=:endTimeStampUTC)
		AND (endTimeStampUTC>=:endTimeStampUTC)
	) OR (
		(startTimeStampUTC<=:startTimeStampUTC)
		AND (endTimeStampUTC>=:endTimeStampUTC)
	) OR (
		(startTimeStampUTC>=:startTimeStampUTC)
		AND (endTimeStampUTC<=:endTimeStampUTC)
	)

)
