		do rs.Execute()
		while rs.Next() {
			set eventID=rs.Get("ID")
			&SQL(SELECT COUNT(ID) INTO :tmp FROM abstract.lnkPresentationMethodEvent WHERE eventID = :eventID)
			if +SQLCODE=0 {
				if tmp>9 {
					write !,ns,?25,eventID
				}
			}
		}
	}
}
