set multiLingualAlts=0
if ##class(setup.objSystemTypes).isModuleAllowed("Multi Lingual Frontend"),##class(setup.objSystemTypes).isModuleAllowed("Multi Lingual Alternatives") {
	set multiLingualAlts=1	
