write !,"	<td class=""black10px ef-session-time"">"
write !,"		<span class=""ef-no-wrap"">",..encodeHTMLContent($Piece(timeString," - ",1))," -</span> <span class=""ef-no-wrap"">",..encodeHTMLContent($Piece(timeString," - ",2)),"</span>"
if ..orderingMode="order by location" {
	if ..hasMultipleDays() {
		set dayNumber=##class(setup.objItem).getDayNumber(..sessionID)
		write "<br>",..encodeHTMLContent(##class(setup.objItem).getDayDescription(..eventID,dayNumber,..languageID))	
	}
}
write !,"	</td>"

if ##class(setup.objEventSetting).getParameter("abstracts","Show abstracts on the website",..eventID) {	
	if ##class(setup.agendaMethods).hasAnyAbstractsAssignedToSessions(..eventID) {
		return 1	
	}
}
return 0

set sessionDetails=##super(sessionID,contextData) set sessionDetails.attendeeCategoryID=contextData.attendeeCategoryID set sessionDetails.currencyID=contextData.currencyID set sessionDetails.tempPersonID=contextData.tempPersonID set sessionDetails.isAmendment=contextData.isAmendment set sessionDetails.tempPurchaseID=contextData.tempPurchaseID set sessionDetails.pricingHorologUTC=contextData.pricingHorologUTC set sessionDetails.guestCount=##class(eCom.tempPerson).getGuestCount(contextData.tempPersonID) set sessionDetails.guestBasketItemID=##class(setup.objItem).getGuestBasketItemID(sessionID)
if contextData.attendeeCategoryID { set sessionDetails.delegateBasketItemID=##class(setup.objItem).getDelegateBasketItemID(sessionID,contextData.attendeeCategoryID) } else { set sessionDetails.delegateBasketItemID=##class(setup.objItem).getDelegateBasketItemID(sessionID) }
return sessionDetails

