do $$$AssertTrue(($IsObject(validator))&&(validator.%IsA("EF.validator.singleLineText")))

set ..errorMessage=""
set isValid=##super(input)
if $l(input) {
	if ..timeFormat=12 {
		if $ZTH(input,4,"")="" {
			set isValid=0
		}
	} elseif ..timeFormat=24 {
		if $ZTH(input,2,"")="" {
			set isValid=0
		}
