set element = ..getElementByIdentifier(identifier)
if $IsObject(element) {
	do element.render()

} elseif $Get(attrs("prowebcomponentname"))'="" {

	if '..contextData.pageID {
		throw ##class(shared.exceptions.generalException).%New("PageID was missing from contextData when attempting to render Proweb tag")
	}

