do rsLists.Execute(pageID)
while rsLists.Next() {
	set prowebListID = rsLists.Get("ID")
	do ..loadProwebListElementsFromDatabase( prowebListID)
}

set rsProwebItems = ##class(%ResultSet).%New("sc.xModules.objList:qGetChildren")
do rsProwebItems.Execute(prowebListID)
while rsProwebItems.Next() {	
	set prowebID = rsProwebItems.Get("ID")
	set typeOfElement = rsProwebItems.Get("dataType")
	if typeOfElement = "objCODitem" {
		set storage = ##class(sc.facade.prowebItemStorageInputElement).createNew(prowebID, ..contextData)

	} elseif typeOfElement = "objText" {
		set storage = ##class(sc.facade.prowebItemStorageTextBlock).createNew(prowebID, ..contextData)

	} elseif typeOfElement = "objMenuBlock" {
		set storage = ##class(sc.facade.prowebItemStorageMenuBlock).createNew(prowebID, ..contextData)

	} elseif typeOfElement = "objFormDivider" {
		set storage = ##class(sc.facade.prowebItemStorageDivider).createNew(prowebID, ..contextData)

	} elseif typeOfElement = "objFileUpload" {
		set storage = ##class(sc.facade.prowebItemStorageFileUpload).createNew(prowebID, ..contextData)

	} elseif typeOfElement = "objFlash" {
		set storage = ##class(sc.facade.prowebItemStorageYouTubeVideo).createNew(prowebID, ..contextData)

	} else {
		throw ##class(shared.exceptions.generalException).%New("Proweb type not implemented: "_typeOfElement)	
	}
	if storage.isOkToUse(..contextData) {			
		set prowebItem = ##class(sc.facade.prowebItem).createNew(storage)
		do ..addControllerForProwebItem(prowebItem)
	}
}

#define isInputElement prowebItem.storage.%Extends("sc.facade.prowebItemStorageInputElement")
#define isTextBlock prowebItem.storage.%Extends("sc.facade.prowebItemStorageTextBlock")
#define isMenuBlock prowebItem.storage.%Extends("sc.facade.prowebItemStorageMenuBlock")
#define isDivider prowebItem.storage.%Extends("sc.facade.prowebItemStorageDivider")
#define isYouTubeVideo prowebItem.storage.%Extends("sc.facade.prowebItemStorageYouTubeVideo")

#dim elementIdentifier as %String = ..getElementIdentifierForProwebItems(prowebItem)
#dim element as EF.pageController.proweb.prowebBase

if $$$isInputElement { 
	set element = ..getNewElementForQuestion(prowebItem)

} elseif $$$isTextBlock {
	set element = ##class(EF.pageController.proweb.textBlock).addElementToParent(..parentListElement, elementIdentifier, prowebItem.storage.text)

} elseif $$$isMenuBlock {
	set element = ##class(EF.pageController.proweb.menuBlock).addElementToParent(..parentListElement, elementIdentifier, prowebItem.storage.listOfMenuItems)

} elseif $$$isDivider {
	set element = ##class(EF.pageController.proweb.divider).addElementToParent(..parentListElement, elementIdentifier, "")

} elseif $$$isYouTubeVideo {
	set element = ##class(EF.pageController.proweb.youTubeVideo).addElementToParent(..parentListElement, elementIdentifier, "")

} else {
	throw ##class(shared.exceptions.generalException).%New("proweb item type not implemented yet: "_prowebItem.storage)	
}

set element.uiComponent.isProwebEditMode = (..contextData.prowebMode = "edit")

set element.uiComponent.prowebItem = prowebItem
set element.prowebItem = prowebItem
set element.prowebID = prowebItem.storage.prowebID
set element.uiComponent.prowebItemID = prowebItem.storage.prowebID
set element.uiComponent.contextData = ..contextData

if (element.#canBeDependent) & (..contextData.isTestMode = 0) {
	set dependsOnAlternativeDescription = ##class(sc.dependencyMethods).getDependsOnAlternativeDescription(prowebItem.storage.prowebID)
	if dependsOnAlternativeDescription '="" {
		set parentProwebID = ##class(sc.dependencyMethods).getDependsOnPrompt(prowebItem.storage.prowebID)

		set storage = ##class(sc.facade.prowebItemStorageInputElement).createNew(parentProwebID, ..contextData)
		set parentProwebItem = ##class(sc.facade.prowebItem).createNew(storage)

		set parentElementIdentifier = ..getElementIdentifierForProwebItems(parentProwebItem)
		set dependencyList = ##class(EF.ui.components.dependencyList).createNew(parentElementIdentifier, dependsOnAlternativeDescription)	
		set element.showHideDependencyList = dependencyList
	}
}
do ..listOfControllerIDsSorted.Insert(element.identifier)

#dim value as %String
#dim elementIdentifier as %String 
#dim labelText as %String = prowebItem.storage.labelText

if (prowebItem.storage.isGuest) && (optionalGuestNumber = "") {
	set optionalGuestNumber = 1
}
set elementIdentifier = ..getElementIdentifierForProwebItems(prowebItem, optionalGuestNumber)
set value = ..getValueFromEComTemp(prowebItem, optionalGuestNumber)

set pageControllerClassName = $Case(prowebItem.storage.inputType,
										"text":"textInput",
										"emailAddress":"emailInput",
										"time":"timeInput",
										"date":"datePicker",
										"textarea":"textArea",
										"phoneNumber":"phoneNumber",
										"integer":"integerInput",
										"floatingPointNumber":"numericInput",
										"webAddress":"webAddress",
										"multiEmailAddress":"emailInputMulti",
										"checkbox":"checkboxGroup",
										"dateRange":"dateRange",
										"password":"passwordInput",
										"fileUpload":"fileUpload",
										:"exception"
									)

if prowebItem.storage.isHiddenToBooker(..contextData) {
	set pageControllerClassName = "privateData" 
}

if pageControllerClassName '= "exception" {		
	set element = $ClassMethod("EF.pageController.proweb."_pageControllerClassName, "addElementToParent", ..parentListElement, elementIdentifier, labelText, value, prowebItem.storage.isReadOnly)

} elseif (prowebItem.storage.inputType = "dropdown") ! (prowebItem.storage.inputType = "radiobutton") ! (prowebItem.storage.inputType = "multibox") {
	set element = ..addProwebSelectionElement(prowebItem, optionalGuestNumber)

} else {
	throw ##class(shared.exceptions.generalException).%New("proweb question type is not implemented yet: "_prowebItem.storage.inputType)	
}

if prowebItem.storage.inputType = "checkbox" {
	set childController = element.addProwebCheckbox(element, elementIdentifier_"-"_1, labelText, value)
}		
if prowebItem.storage.inputType = "dateRange" {
	do ..addSecondDatePickerForDateRange(prowebItem, element)
}
if (element.%IsA("EF.pageController.proweb.checkboxGroup") = 0) && (+optionalGuestNumber = 0) {
	set element.isMandatory = +prowebItem.storage.isMandatory
	set element.uiComponent.isMandatory = +prowebItem.storage.isMandatory
	set element.uiComponent.validator.isMandatory = +prowebItem.storage.isMandatory
}

if $Length(prowebItem.storage.customValidation) {
	if prowebItem.storage.customValidation = "#personRegisteredWithSameAnswer" {
		set element.uiComponent.validator = ##class(EF.validator.personRegisteredWithSameAnswer).%New()
		set element.uiComponent.validator.prowebID = prowebItem.storage.prowebID

	} else {
		set element.uiComponent.validator = ##class(EF.validator.patternMatchValidation).%New()
		set element.uiComponent.validator.pattern = prowebItem.storage.customValidation
	}

} elseif $Length(prowebItem.storage.bespokeValidationMethod) {
	set element.uiComponent.validator = ##class(EF.validator.bespokeValidationMethod).%New()
	set element.uiComponent.validator.bespokeValidationMethod = prowebItem.storage.bespokeValidationMethod
	set element.uiComponent.validator.databaseItemName = prowebItem.storage.databaseItemName
	set element.uiComponent.validator.tempPersonID = ..contextData.tempPersonID
}
if $Length(prowebItem.storage.validationErrorMessage) {
	set element.uiComponent.validator.errorMessage = prowebItem.storage.validationErrorMessage	
}

set element.uiComponent.isGuest = prowebItem.storage.isGuest
if prowebItem.storage.isGuest {
	set element.guestNumber = optionalGuestNumber	
	set element.isGuest = 1
	set element.uiComponent.guestNumber = optionalGuestNumber	
}
set element.uiComponent.helpText = prowebItem.storage.helpText	

return element

#define getRawData 1
#dim element as EF.pageController.element
#dim value as %String
#dim elementIdentifier as %String = ..getElementIdentifierForProwebItems(prowebItem)
#dim labelText as %String = prowebItem.storage.labelText
#dim itemName as %String = prowebItem.storage.databaseItemName
#dim isReadOnly as %Boolean = prowebItem.storage.isReadOnly
#dim counter as %Integer = 0
#dim getRawData as %Boolean = 1
#dim key as %Integer = ""

if prowebItem.storage.inputType = "dropdown" {
	set value = ..getValueFromEComTemp(prowebItem, optionalGuestNumber)
	set element = ##class(EF.pageController.proweb.dropdown).addElementToParent(..parentListElement, elementIdentifier, labelText, value, isReadOnly)
	set element.uiComponent.isSearchAsYouType = prowebItem.storage.isSearchAsYouType

} elseif prowebItem.storage.inputType = "radiobutton" {
	set value = ..getValueFromEComTemp(prowebItem, optionalGuestNumber)
	set element = ##class(EF.pageController.proweb.radioButtons).addElementToParent(..parentListElement, elementIdentifier, labelText, value, isReadOnly)

} elseif prowebItem.storage.inputType = "multibox" {
	set value = ..getValueFromEComTemp(prowebItem, optionalGuestNumber, $$$getRawData)
	set element = ##class(EF.pageController.proweb.checkboxGroup).addElementToParent(..parentListElement, elementIdentifier, labelText, value)
	set element.uiComponent.columns=1

} else {
	throw ##class(shared.exceptions.generalException).%New("Proweb selection type not implemented")
}

set element.prowebID = prowebItem.storage.prowebID	

for  {
	set alternative = prowebItem.storage.listOfAlternatives.GetNext(.key)
	if key ="" quit

	if prowebItem.storage.inputType = "multibox" {
		set counter = counter+1
		set isChecked = ##class(EF.pageController.proweb.checkboxGroup).isAlternativeInValueString(alternative.displayText, value)
		set childController = element.addProwebCheckbox(element, elementIdentifier_"-"_counter, alternative.displayText, isChecked)

	} else {
		set childController = element.addAlternative(alternative.value, alternative.displayText)	
	} 
}

if (value = "") && (prowebItem.storage.inputType = "radiobutton" ) {
	set element.value = prowebItem.storage.listOfAlternatives.GetAt(1).value
}
return element

