#dim key as %Integer = ""
#dim lastUsedKey as %String = ""
#dim element as EF.pageController.element
#dim hasFoundGuestUiComponent as %Boolean = 0
#define isGuestElement (element.uiComponent.isGuest = 1)
#define isNotGuestElement (+element.uiComponent.isGuest = 0)

for  {
	set elementID = ..listOfControllerIDsSorted.GetNext(.key)
	if key="" quit

	set lastUsedKey = key
	set element = ..getElementByIdentifier(elementID)
	if '$IsObject(element) {
		throw ##class(shared.exceptions.generalException).%New("Could not find elementID: "_elementID)	
	}
	if element.%Extends("EF.pageController.proweb.inputElement") {
		if $$$isGuestElement  {
			do ..listOfGuestControllerIDs.Insert(elementID)
			set hasFoundGuestUiComponent = 1
		} 

		if $$$isNotGuestElement && (hasFoundGuestUiComponent = 1) {
			set hasFoundGuestUiComponent = 0
			set ..positionOfNextNonGuestQuestion = key
		}			

	} else { // non-questions
		if hasFoundGuestUiComponent = 1 {
			set hasFoundGuestUiComponent = 0
			set ..positionOfNextNonGuestQuestion = lastUsedKey
		}
	}
}
if '..positionOfNextNonGuestQuestion {
	set ..positionOfNextNonGuestQuestion = lastUsedKey+1 // end of page
}

#dim key as %Integer = ""
#dim element as EF.pageController.element
#dim eventConfiguration as setup.eventConfiguration = ##class(setup.eventConfiguration).createForEventID(..contextData.eventID)
#dim maxGuests as %Integer = eventConfiguration.getMaxGuestsPerAttendee()

if ..contextData.isTestMode {
	set maxGuests = ..contextData.maxGuests
}
if (maxGuests)&&(..contextData.prowebMode = "edit") {
	set maxGuests = 1	
}
for guestNumber = 2:1:maxGuests {
	for  {
		set elementID = ..listOfGuestControllerIDs.GetNext(.key)
		if key="" quit
		set element = ..getElementByIdentifier(elementID)

		set newController = ..getNewElementForQuestion(element.uiComponent.prowebItem, guestNumber)

		set newController.uiComponent.isProwebEditMode = (..contextData.prowebMode = "edit")
		set newController.uiComponent.prowebItem = element.uiComponent.prowebItem
		set newController.uiComponent.contextData = ..contextData

		do ..listOfControllerIDsSorted.InsertAt(newController.identifier, ..positionOfNextNonGuestQuestion) 

		set ..positionOfNextNonGuestQuestion = ..positionOfNextNonGuestQuestion + 1
		kill newController
	}
}

#dim key as %Integer = ""
#dim lastUsedKey as %String = ""
#dim element as EF.pageController.element
#dim currentGuestNumber as %Integer = 0
#define isGuestElement (element.uiComponent.isGuest = 1)
#define isNotGuestElement (+element.uiComponent.isGuest = 0)

for  {
	set elementID = ..listOfControllerIDsSorted.GetNext(.key)
	if key="" quit
	set element = ..getElementByIdentifier(elementID)
	set lastUsedKey = key

	if element.%Extends("EF.pageController.proweb.inputElement") {

		if $$$isGuestElement && (currentGuestNumber = 0) {
			set currentGuestNumber = element.guestNumber
			do ..insertControllerForGuestPanel(key, "opening")
		}

		if $$$isNotGuestElement && (currentGuestNumber) {
			set currentGuestNumber = 0
			do ..insertControllerForGuestPanel(key, "closing") 
		}

		if $$$isGuestElement && (+element.guestNumber '= +currentGuestNumber) {
			set currentGuestNumber = 0
			do ..insertControllerForGuestPanel(key, "closing") 
		}

	} else { // non-questions
		if currentGuestNumber {
			set currentGuestNumber = 0
			do ..insertControllerForGuestPanel(key, "closing") 
		}			
	}
}

if currentGuestNumber {
	do ..insertControllerForGuestPanel(lastUsedKey+1, "closing") 
}

#dim controllerForPanel as EF.pageController.proweb.panel
set controllerForPanel = ##class(EF.pageController.proweb.panel).addElementToParent(..parentListElement, "GuestPanel-"_insertPosition, "") if $IsObject(controllerForPanel) { set controllerForPanel.isOpeningOrClosing = openOrClose set controllerForPanel.uiComponent.isOpeningOrClosing = openOrClose set controllerForPanel.uiComponent.contextData = ..contextData
do ..listOfControllerIDsSorted.InsertAt(controllerForPanel.identifier, insertPosition) }

#dim element as EF.pageController.element
#dim hasRenderedAtLeastOne as %Boolean = 0

for position = 1:1:..listOfControllerIDsSorted.Count() {
	set elementID = ..listOfControllerIDsSorted.GetAt(position)

	set element = ..getElementByIdentifier(elementID)
	if element.elementParent = parentListElement {
		if element.isOkToRender() {
			if (hasRenderedAtLeastOne = 0) && (element.uiComponent.%Extends("EF.ui.components.frontend.textInput")) && (..contextData.prowebMode '= "edit"){
				set element.uiComponent.hasAutoFocus = 1 	
			}
			do element.uiComponent.render()		
			set hasRenderedAtLeastOne =1
		}
	}
}

if 'hasRenderedAtLeastOne {
	do ..renderEmptyListHTML()	
}

#dim key as %Integer = ""
#dim element as EF.pageController.element
#dim prowebItemID as %Integer

set displayOrder = 0
for index = 1:1:$Length(orderedElementIds,"|") {
	set prowebItemID = $Piece(orderedElementIds,"|",index)
	if $Length(prowebItemID) {
		set displayOrder = displayOrder + 1
		set prowebItems(prowebItemID) = displayOrder
	}
}

for {
	set element = ..allPageElements.GetNext(.key)
	if key = "" quit
	if element.%Extends("EF.pageController.proweb.prowebBase") {
		if element.prowebItem.storage.prowebID {
			try {
				if $Data(prowebItems(element.prowebItem.storage.prowebID)) {
					set prowebElement = ##class(sc.objData).%OpenId(element.prowebID)
					set oldDisplayOrder = prowebElement.displayOrder
					set newDisplayOrder = prowebItems(element.prowebID)
					set prowebElement.displayOrder = prowebItems(element.prowebID)
					do prowebElement.%Save()

					if (oldDisplayOrder '= newDisplayOrder) {
						set xUserID = $Get(%session.Data("eventsforce","backend","xPersonID"))
						set eventID = prowebElement.objEvent.%Id()
						set pageID = ##class(sc.xModules.objCODitem).getPageIDforQuestion(prowebElement.%Id())
						set pageName = ##class(sc.xModules.objPage).getDescription(pageID)
						set prowebItemDescription = ##class(sc.xModules.objCODitem).getCODItemName(prowebElement.%Id())
						if (prowebElement.%Id() && pageID) {
							do ##class(setup.objEventAudit).add(xUserID, eventID, "Reg Page '"_pageName_"' ("_pageID_"), Changed '"_prowebItemDescription_"' Registration Question Order: "_newDisplayOrder_" (New Value) - "_oldDisplayOrder_" (Old Value)")
						}
					}
				}
			} catch error {
				throw ##class(shared.exceptions.generalException).%New("Failed to get the prowebID of element: "_element)	
			}
		} else {
			write !,"element without prowebID:",element	
		}
	}
}

