new okToWrite,rs,sqltext,menuPageID,homePageID,bannerPageID,secondaryBannerPageID,layoutTemplateID,sectionID,before,after,curPageName,isSurvey,surveyID,curPageID,externalLoginPage,standardDesingID
if from["{{" set from=$TR(from,"{","[")
if from["}}" set from=$TR(from,"}","]")
if to["{{" set to=$TR(to,"{","[")
if to["}}" set to=$TR(to,"}","]")

// don't write out the bits when in edit mode
set backendEdit=0
