 while dataID'="" {
 	&SQL(SELECT parent INTO :dataID FROM sc.objData WHERE ID = :dataID)
 	if ..%ExistsId(dataID) {
 		set objData=..%OpenId(dataID)
 			if objData.dataType="objImage" {
