	}
}

#dim isReadOnly as %Boolean = 0
#dim prowebID as %Integer = prowebItem.%Id()
#dim itemName as %String = ##class(sc.xModules.objCODitem).getCODItemName(prowebID)
#dim value as %String
#dim elementIdentifier as %String 
