set rs=##class(%ResultSet).%New("%Library.DynamicQuery:SQL")
do rs.Prepare("SELECT ID,dataType FROM sc.objData WHERE (parent=?) AND (active=1)")
do rs.Execute(dataID)
while rs.Next() {
	if (rs.Get("dataType")="objGroup")!(rs.Get("dataType")="objPage") {
