if ..isEditMode { do ##class(EF.ui.components.frontend.editorControlPanel).outputEditorControlPanel($this) }
do ##class(layout.objTemplate).writeBit("{{ef_header}}","{{ef_banner}}") do ##class(layout.objTemplate).writeBit("{{ef_banner}}","{{ef_main_progress}}")
if ..showRegistrationProgressBar { Do ..outputProgressBar() }
do ##class(layout.objTemplate).writeBit("{{ef_main_progress}}","{{ef_main_header}}") do ..outputPageHeader() do ##class(layout.objTemplate).writeBit("{{ef_main_header}}", "{{ef_main_content}}")

if ..isEditMode  {
	&html<
