#dim key as %Integer = "" #dim element as EF.pageController.element #dim maxGuests as %Integer = ..page.getEventConfiguration().getMaxGuestsPerAttendee() #dim prowebID = $Piece(..identifier,"-",2)
for guestNumber = 2:1:maxGuests { set newGuestPanel = ..createGuestPanel(..elementParent,prowebID, guestNumber) set key = "" for { set elementID = ..listOfControllerIDsSorted.GetNext(.key) if key="" quit set element = ..page.getElementByIdentifier(elementID) set newController = newGuestPanel.addElementForProwebItem(element.uiComponent.prowebItem, guestNumber)
