s fn=$ZCVT(fn(i),"L") if fn=page!(page="*") { w !,"Loading:",fn,"..." if ##class(%Library.File).Exists(path_fn) { w " result=",##class(layout.objTemplate).loadTemplate(de(i),in(i),1,i,path_fn,1,0,img(i),level(i),source(i),directory(i),$G(efLogo(i)),$G(layout(i)),$G(design(i))) } else { w " file not found" } } s fnP=$P(fn,".html",1)_"_popup.html" if fn=page!(page="*"),##class(%Library.File).Exists(path_fnP) { // if we don't have a popup template, doesn't matter, the normal one will be used w !,"Loading:",fnP,"..." w " result=",##class(layout.objTemplate).loadTemplate(de(i)_"_popup",in(i)_"_popup",1,i,path_fnP,1,1,img(i),level(i),source(i),directory(i),$G(efLogo(i)),$G(layout(i)),$G(design(i))) } }
