	set xLangID=rs.Get("ID")

	// find the page that contains the homepage
	&SQL(SELECT homePageID INTO :tmp FROM setup.objEventDetails WHERE objEvent = :xEventID AND objLanguage = :xLangID)
	if +SQLCODE=0 {
		set homePageID=tmp,tmp2=""
