	 			if (+SQLCODE = 0) { // yes we do
	 				set hasChildren=1
	 			}	
	 		}
	 		if (hasChildren) {
		 		&SQL(SELECT description INTO :tmp FROM sc_xModules.objCODitem WHERE ID = :myID)
