	 				set hasActiveData=##class(cod.objItemData).findActiveData(promptName,eventID)
	 				if (hasActiveData=1) {
		 				set ok=0	
	 				}
		 		}
	 		}
 		}
 		if ok=0 quit
 	}
	quit ok

