do rsSoft.Execute()
while rsSoft.Next() {
	if '$l(##class(setup.objEventData).getData(xEventID,rsSoft.Get("ID"))) {
		set out($I(out))=##class(shared.pCSP).translateAdmin("Not all mandatory event settings have been completed")
	}
}

