// lock down the setting if we have any bookings if ##class(links.lnkPersonEvent).hasAnyBookings(xEventID) { if $P(style,"|",1)="active button" set style="pre-selected button|lock_active.png" if $P(style,"|",1)="inactive button" set style="disabled button|lock_disabled.png" } quit style

